;var customSelect = {

  selectSelector: '[data-customSelectSelect]',
  overlaySelector: '[data-customSelectOverlay]',

  update: function(select){

    // Get the related select element...
    var overlay = select.siblings(this.overlaySelector).first();
    var selectedItem = select.find('option:selected').first();

    // Update the overlay text with selected option...
    overlay.html( selectedItem.text() );

  }

};


// Update on document ready
$(document).ready(function() {
  $(customSelect.selectSelector).each(function(){
    customSelect.update($(this));
  });
});


// Update on select value change
$(document).on('change', customSelect.selectSelector, function() {
  customSelect.update($(this));
});
