// Add some global user events to the queue
queue.documentLoad.push(function scrollMetadata(){

  $('html').on('click', function(event) {
    queue.trigger('globalClick');
  });

  App.lightResize(function(){
    queue.trigger('globalResize');
  }, 300);

  App.lightScroll(function(){
    queue.trigger('globalScroll');
  }, 300);

});


// Execute everything on document load
documentLoad();
function documentLoad(){
  if(App.DOMContentLoaded === true){
    queue.trigger('documentLoad');
  } else {
    setTimeout(documentLoad, 25);
  }
}
